import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import API from '../api';
import '../Styles.css';

function ConfirmBooking() {
  const { roomId } = useParams(); // ðŸ‘ˆ grabs room ID from URL
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const today = new Date().toISOString().split('T')[0]; // ðŸ‘ˆ sets today's date to prevent past selection

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (endTime <= startTime) {
      setMessage("End time must be after start time.");
      return;
    }

    try {
      const token = localStorage.getItem('token'); // ðŸ‘ˆ gets token from local storage

      await API.post(
        '/api/reservations/',
          {
            room: parseInt(roomId),   // ðŸ‘ˆ converts to integer if needed by backen
            date,
            start_time: startTime,
            end_time: endTime,
          },
          {
            headers:
                {
                  Authorization: `Token ${token}`,
                },
          }
          );
      setMessage("Reservation confirmed!");
      setTimeout(() => navigate('/mybookings'), 1500); // ðŸ‘ˆ optional redirect
    } catch (err) {
      console.error("Error details:", err.response?.data || err.message);
      setMessage("Reservation failed.");
    }
  };

  return (
    <>
      <header>
        <h1>Confirm Your Reservation</h1>
        <p>You're booking room ID: {roomId}</p>
      </header>

      {message && (
        <div
          style={{
            textAlign: 'center',
            fontWeight: 'bold',
            color: message.includes("failed") ? "red" : "green",
            marginTop: '10px',
          }}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>Date:
          <input type="date" min={today} value={date} onChange={(e) => setDate(e.target.value)} required />
        </label>
        <label>Start Time:
          <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} required />
        </label>
        <label>End Time:
          <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} required />
        </label>
        <button type="submit" className="btn btn-green">Confirm Reservation</button>
      </form>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <button className="btn-yellow" onClick={() => navigate('/rooms')}>
          Back to Available Rooms
        </button>
      </div>
    </>
  );
}

export default ConfirmBooking;
